import osimport csvcsvpath = os.path.join('/Users/Clayton/Documents/JHU_Bootcamp/python-challenge/PyBank/Resources/budget_data.csv')total = 0months = 0previous = 0total_change = 0    with open(csvpath) as csvfile:    csvreader =csv.reader(csvfile)    #moves the for loop one row so header is not counted in calculations    next(csvreader)    for row in csvreader:        #counts each row and adds to "months" to get total months        months = months + 1        #adds the value of "Profit/Losses to get total, adjusts type to integer        total = total + int(row[1])                #creats a list of the changes in value for each month        change = []        delta = int(row[1]) - previous                change.append(delta)        previous = int(row[1])                #performs a for loop to calculate total change and average change        for row in change:            total_change = total_change + change[0]    avg_change = (total_change-867884)/(months-1)        #references the change list to pull the values of the max and min change    max_inc = max(change)    min_inc = min(change)print("Financial Anlysis",      "---------------------",      "Total Months: " + str(months),      "Total: " + str(total),      "Average Change: " + str(avg_change),      "Greatest Increase in Profits: " + str(max_inc),      "Greatest Decrease in Profits: " + str(min_inc))myText = open('/Users/Clayton/Documents/JHU_Bootcamp/python-challenge/PyBank/Analysis/PyBank.txt', 'w')myString = str("Financial Anlysis",            "---------------------",            "Total Months: " + str(months),            "Total: " + str(total),            "Average Change: " + str(avg_change),            "Greatest Increase in Profits: " + str(max_inc),            "Greatest Decrease in Profits: " + str(min_inc))myText.write(myString)myText.close()